import '../css/Menu.css';
import React, { Component } from 'react'
import Burger from 'react-css-burger';



class Header extends Component {
  state = {
    active: false,
  };

  render() {

    if (state===false){
      return (
        <div>
          <header className="App-header">
            <div className="header">
              <h1 className="header-text">Thomas Birmingham |</h1>
              <h2 className="header-text">Developpeur Front-End</h2>
              <h2 className="header-text">| Epitech 2022 Paris</h2>
            </div>
          </header>
          <Burger
            onClick={() => this.setState({ active: !this.state.active })}
            active={this.state.active}
            burger="minus"
            color="#dc3ab8c9"
            hoverOpacity={0.8}
            scale={1.2}
          />
        </div>
      );
    }
    else{
      return (
        <div>
          <header className="App-header">
            <div className="header">
              <h1 className="header-text">Thomas Birmingham |</h1>
              <h2 className="header-text">Developpeur Front-End</h2>
              <h2 className="header-text">| Epitech 2022 Paris</h2>
            </div>
          </header>
          <Burger
            onClick={() => this.setState({ active: !this.state.active })}
            active={this.state.active}
            burger="minus"
            color="#dc3ab8c9"
            hoverOpacity={0.8}
            scale={1.2}
          />
          <nav className="menu">
            <ul>
              <li><a href="/Presentation" className="menu-text">Home</a></li>
              <li><a href="/Presentation" className="menu-text">Presentation</a></li>
              <li><a href="/ProjetsPro" className="menu-text">P_ Pro</a></li>
              <li><a href="/ProjetsEpitech" className="menu-text">P_ Epitech</a></li>
              <li><a href="/ProjetsPerso" className="menu-text">P_ Perso</a></li>
            </ul>
          </nav>
        </div>
      );
    }

  }
}

