import React from 'react';
import logoReact from '../img/logo192.png';
import '../css/Carrousel.scss';


function ProjetsPro() {

	var carousel = document.querySelector('.carousel');
	var figure = carousel.querySelector('figure');
	var nav = carousel.querySelector('nav');
	var numImages = figure.childElementCount;
	var theta =  2 * Math.PI / numImages;
	var currImage = 0;

    nav.addEventListener('click', onClick, true)

    function onClick(e) {
        e.stopPropagation();

        var t = e.target;
        if (t.tagName.toUpperCase() !== 'BUTTON')
            return;

        if (t.classList.contains('next')) {
            currImage++;
        }
        else {
            currImage--;
        }

        figure.style.transform = `rotateY(${currImage * -theta}rad)`;
    }

    return (
      <div className="">
          <h2>ProjetsPro</h2>
          <div class="carousel">
                <figure>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                    <img src={logoReact} alt="test"></img>
                </figure>
                <nav>
                    <button class="nav prev">Prev</button>
                    <button class="nav next">Next</button>
                </nav>
            </div>
      </div>
    )
  }

export default ProjetsPro;
